mapper.select:
SELECT COUNT(*) FROM CRM_PERSON A, CRM_PERSON_ADDITION B WHERE A.IS_USED = 'Y' AND A.CUSTOMER_NO = B.CUSTOMER_NO
AND ${SEARCH_FIELD} LIKE CONCAT('%', #{SEARCH_STRING}, '%')
AND ${SEARCH_FIELD} = #{SEARCH_STRING}
AND ${SEARCH_FIELD} != #{SEARCH_STRING}

SELECT A.CUSTOMER_NO, A.CUSTOMER_NAME, B.COMPANY_NO, C.COMPANY_NAME, B.CUSTOMER_DEPT, B.CUSTOMER_POSITION, B.CUSTOMER_COMPANY_TEL, B.CUSTOMER_PHONE, C.COMPANY_FAX, B.CUSTOMER_EMAIL, B.LAST_VISITED_DATE FROM CRM_PERSON A, CRM_PERSON_ADDITION B, CRM_COMPANY C WHERE A.IS_USED = 'Y' AND A.CUSTOMER_NO = B.CUSTOMER_NO AND B.COMPANY_NO = C.COMPANY_NO
AND ${SEARCH_FIELD} LIKE CONCAT('%', #{SEARCH_STRING}, '%')
AND ${SEARCH_FIELD} = #{SEARCH_STRING}
AND ${SEARCH_FIELD} != #{SEARCH_STRING}
ORDER BY ${SIDX} ${SORD}
ORDER BY A.CUSTOMER_NAME
LIMIT #{BEGIN_INDEX}, #{ROWS}

SELECT 
	COUNT(*)
FROM 
	CRM_VISITED_SHEET A
WHERE
	A.VISITED_DATE > #{START_DATE} 
	AND A.IS_USED = 'Y'
	AND A.VISITED_DATE < #{END_DATE}

	AND A.COMPANY_NO = #{COMPANY_NO}


	AND A.SHEET_NO IN (SELECT SHEET_NO FROM CRM_VISITED_EMPLOYEE WHERE EMPLOYEE_NO = #{EMPLOYEE_NO})

SELECT A.SHEET_NO, A.CUSTOMER_NO, A.COMPANY_NO, B.COMPANY_NAME, C.CUSTOMER_NAME, A.VISITED_DATE, A.VISITED_MEMO, CONCAT(DATEDIFF(CURDATE(), VISITED_DATE), 'Ïùº') DATE_GAP, VISITED_GUBUN FROM CRM_VISITED_SHEET A, CRM_COMPANY B, CRM_PERSON C WHERE A.VISITED_DATE > #{START_DATE} AND A.VISITED_DATE < #{END_DATE} AND A.IS_USED = 'Y'
AND A.COMPANY_NO = #{COMPANY_NO}
AND A.SHEET_NO IN (SELECT SHEET_NO FROM CRM_VISITED_EMPLOYEE WHERE EMPLOYEE_NO = #{EMPLOYEE_NO})
AND A.COMPANY_NO = B.COMPANY_NO AND A.CUSTOMER_NO = C.CUSTOMER_NO
ORDER BY ${SIDX} ${SORD}
ORDER BY A.VISITED_DATE DESC
LIMIT #{BEGIN_INDEX}, #{ROWS}

SELECT COUNT(*) FROM CRM_COMPANY WHERE IS_USED = 'Y'
AND ${SEARCH_FIELD} LIKE CONCAT('%', #{SEARCH_STRING}, '%')
AND ${SEARCH_FIELD} = #{SEARCH_STRING}
AND ${SEARCH_FIELD} != #{SEARCH_STRING}

SELECT COMPANY_NO, COMPANY_NAME, COMPANY_TEL, COMPANY_FAX, COMPANY_ADDR, IS_USED FROM CRM_COMPANY WHERE IS_USED = 'Y'
AND ${SEARCH_FIELD} LIKE CONCAT('%', #{SEARCH_STRING}, '%')
AND ${SEARCH_FIELD} = #{SEARCH_STRING}
AND ${SEARCH_FIELD} != #{SEARCH_STRING}
ORDER BY ${SIDX} ${SORD}
ORDER BY COMPANY_NAME
LIMIT #{BEGIN_INDEX}, #{ROWS}
